var link=document.querySelector(".search h2"),form=document.querySelector(".search-hotel"),arrival=form.querySelector("[name=date-arrival]"),check_out=form.querySelector("[name=check-out]"),storage=localStorage.getItem("arrival");link.addEventListener("click",function(a){a.preventDefault(),form.classList.toggle("search-hotel-open"),arrival.focus(),storage?(arrival.value=storage,check_out.focus()):arrival.focus()}),form.addEventListener("submit",function(a){arrival.value&&check_out.value?localStorage.setItem("arrival",arrival.value):a.preventDefault()}),window.addEventListener("keydown",function(a){27===a.keyCode&&form.classList.contains("search-hotel-open")&&form.classList.remove("search-hotel-open")});